import { Injectable } from '@angular/core';

export interface Furniture {
  name: string;
  category: string;
  price: number;
  imageUrl: string;
}

@Injectable({
  providedIn: 'root'
})
export class FurnitureListService {
  // Define the initial furniture list
  private furnitureList: Furniture[] = [
    { name: 'Wooden Chair', category: 'Chair', price: 45.99, imageUrl: 'data:image/jpeg;base64,' },
    { name: 'Dining Table', category: 'Table', price: 199.99, imageUrl: 'data:image/jpeg;base64,' },
    { name: 'Leather Sofa', category: 'Sofa', price: 599.99, imageUrl: 'data:image/jpeg;base64,' }
  ];
  constructor() { }

  getFurnitureList(): Furniture[] {
    return this.furnitureList;
  }

  addFurniture(furniture: Furniture) {
    this.furnitureList.push(furniture);
  }

  removeFurniture(index: number) {
    this.furnitureList.splice(index, 1);
  }
}
